actionConfiguration.body:
() => {
  const uniqueTags = [];
  const uniqueIds = [];
  GetPlatforms.data.results.map(item => item.tags).forEach(tags => {
    tags.forEach(tag => {
      if (!uniqueIds.includes(tag.id)) {
        uniqueIds.push(tag.id);
        uniqueTags.push(tag);
      }
    });
  });
  return uniqueTags.map(tag => {
    const obj = Object.assign({}, tag);
    obj['label'] = tag.value;
    obj['value'] = tag.id;
    return obj;
  });
}

